function mock(input) {
    if (!input) throw new Error(`Please provide a valid input.`)

    const str = input.toLowerCase()

    var newWord = ""
    const letter = ""

    for (let i = 0; i < str.length; i++) {
        let pick = Math.floor(Math.random() * 2 + 1);

        if (pick == 1) {
            let letter = str[i].toLowerCase();

            newWord = newWord.concat(letter);
        } else {
            let letter = str[i].toUpperCase();

            newWord = newWord.concat(letter);
        }
    }
    return newWord
}
module.exports = mock