function tinyText(input) {
    if (!input) throw new Error(`Please provide a valid input.`)

    function cursive(text) {
        let tiny = {
            a: "ᵃ",
            b: "ᵇ",
            c: "ᶜ",
            d: "ᵈ",
            e: "ᵉ",
            f: "ᶠ",
            g: "ᵍ",
            h: "ʰ",
            i: "ᶦ",
            j: "ʲ",
            k: "ᵏ",
            l: "ˡ",
            m: "ᵐ",
            n: "ⁿ",
            o: "ᵒ",
            p: "ᵖ",
            q: "ᵠ",
            r: "ʳ",
            s: "ˢ",
            t: "ᵗ",
            u: "ᵘ",
            v: "ᵛ",
            w: "ʷ",
            x: "ˣ",
            y: "ʸ",
            z: "ᶻ",
            A: "ᴬ",
            B: "ᴮ",
            C: "ᶜ",
            D: "ᴰ",
            E: "ᴱ",
            F: "ᶠ",
            G: "ᴳ",
            H: "ᴴ",
            I: "ᴵ",
            J: "ᴶ",
            K: "ᴷ",
            L: "ᴸ",
            M: "ᴹ",
            N: "ᴺ",
            O: "ᴼ",
            P: "ᴾ",
            Q: "ᵠ",
            R: "ᴿ",
            S: "ˢ",
            T: "ᴶ",
            U: "ᵁ",
            V: "ⱽ",
            W: "ᵂ",
            X: "ˣ",
            Y: "ʸ",
            Z: "ᶻ"
        };
        return text
            .split("")
            .map(function (a) {
                return tiny.hasOwnProperty(a) ? tiny[a] : a;
            })
            .join("");
    }
    return cursive(input)
}
module.exports = tinyText