function vaporwave(text) {
    if (!text) throw new Error(`Please provide some text.`)

    const vaporwavefied = text.split('').map(char => {
        const code = char.charCodeAt(0)
        return code >= 33 && code <= 126 ? String.fromCharCode((code - 33) + 65281) : char
    }).join('')
    return vaporwavefied
}
module.exports = vaporwave